import "./App.css";
import { ethers } from "ethers";
import { Component } from "react";
import Staking from "./contracts/build/Staking.json";
import StakingTask from "./contracts/build/StakingTask.json";
import { useEffect } from "react";
//import BigNumber from 'bignumber.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      provider: null,
      account: null,
      contract: null,
      myBalance: 0,
      stakedBalance: 0,
      timer: 0,
      stakeAmount: 0,
      myStakedAmount: 0,
      withdrawAmount: 0,
    };
  }

  async componentDidMount() {
    await this.loadWallet();
    await this.setContract();
    await this.getAPRandTimer();
    await this.getStakedBalance();
    await this.myStakedAmount();
  }

  loadWallet = async () => {
    // A Web3Provider wraps a standard Web3 provider, which is
    // what MetaMask injects as window.ethereum into each page
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    // // MetaMask requires requesting permission to connect users accounts
    await provider.send("eth_requestAccounts", []);
    const accounts = await provider.listAccounts();
    const account = accounts[0];
    const balance = await provider.getBalance(account);
    const balanceInEth = ethers.utils.formatEther(balance);

    this.setState({
      provider: provider,
      account: account,
      myBalance: balanceInEth,
    });
  };

  setContract = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum);

    //const networkId = await ethers.net.getId();
    const { chainId } = await provider.getNetwork();

    const networkData = Staking.networks[5777];

    if (networkData) {
      const abi = StakingTask.abi;
      const contractAddress = "0x571f830C36EAFAe5d11654211636291fa0e460A9";
      console.log("abiTask", abi);
      const signer = provider.getSigner();
      const contract = await new ethers.Contract(contractAddress, abi, signer);

      //const gasLimit = new BigNumber("300000");
      // const result = await contract.deposit({gasLimit: new BigNumber("300000")});
      this.setState({ contract: contract });
    }
  };

  getStakedBalance = async () => {
    const contract = this.state.contract;
    const stakedBalance = await contract.totalStakedToken();
    const balanceInEth = ethers.utils.formatEther(stakedBalance);

    const bonusendblock = await contract.bonusEndBlock();
    const startblock = await contract.startBlock();
    const remainingBlocks = bonusendblock - startblock;

    const remainingTimeInSeconds = remainingBlocks * 15; // Assuming `blockTime` is the time in seconds between blocks
    const remainingDays = remainingTimeInSeconds / (60 * 60 * 24);

    const stakedTokenSupply = await contract.stakedToken({
      from: this.state.account,
    });
    const stakedTokenSupplyEth = ethers.utils.formatEther(stakedTokenSupply);
    // if (stakedTokenSupply == 0) {
    //     return 0;  // No staked tokens, APR is 0
    // }

    const blocksPerYear = 365 / 15;
    const rewardperblock = await contract.rewardPerBlock();
    const annualReward = rewardperblock * blocksPerYear;
    const precision = await contract.PRECISION_FACTOR();
    const aprval = (annualReward * precision) / stakedTokenSupplyEth;

    // const remainingBlocks = await contract.bonusEndBlock().then();
    // const remainingTimeInSeconds = remainingBlocks * 15; // Assuming `blockTime` is the time in seconds between blocks

    // const bonusEndBlockt = await contract.bonusEndBlock().call();

    const times = 6;
    this.setState({
      stakedBalance: balanceInEth,
      timer: remainingDays,
      APR:aprval,
    });
  };

  // stake = async () => {
  //   const contract = this.state.contract;
  //   await contract.deposit(this.state.stakeAmount, {
  //     from: this.state.account,
  //     value: ethers.utils.parseEther(this.state.stakeAmount),
  //   });
  // };


getAPRandTimer=async()=>{
await console.log("heeloji");
}




  stake = async () => {
    const contract = this.state.contract;
    await contract.deposit(this.state.stakeAmount, { gasLimit: 750000 });
    // await contract.deposit(this.state.stakeAmount).send({ from: this.state.account });
  };

  withdraw = async () => {
    const contract = this.state.contract;
    await contract.withdraw(this.state.withdrawAmount);
  };

  myStakedAmount = async () => {
    const contract = this.state.contract;
    const amount = await contract.stakedToken({ from: this.state.account });
    const poollimit = await contract.poolLimitPerUser();
    const balancepool = ethers.utils.formatEther(poollimit);
    const balanceInEth = ethers.utils.formatEther(amount);
    this.setState({ myStakedAmount: balanceInEth });
  };

  handleChange = (event) => {
    this.setState({ stakeAmount: event.target.value });
  };



  render() {
    return (
      <div className="App">
        Hello {this.state.account}
        <div>
          <b>Balance:</b> {this.state.myBalance}
          <span>Timer: {this.timer} <br /></span>
          <span>APR: {this.apr}% <br /></span>
        </div>
        <div>
          <b>My Staked Amout:</b> {this.state.myStakedAmount} 
          <button onClick={this.myStakedAmount}>Refresh</button>
        </div>
        <div>
          <b>Total Staked Amount: </b> {this.state.stakedBalance}{" "}
          <button onClick={this.getStakedBalance}>Refresh</button>
        </div>
        <div>
          <div>
            <input
              type="text"
              id="amount"
              name="amount"
              onChange={this.handleChange}
              value={this.state.stakeAmount}
              autoComplete="off"
            ></input>
            <button onClick={this.stake}>Stake</button>
          </div>
          <input
            type="text"
            id="amountWithdraw"
            name="amountWithdraw"
            onChange={this.handleChange}
            value={this.state.withdrawAmount}
            autoComplete="off"
          ></input>
          <button onClick={this.withdraw}>Withdraw</button>
        </div>
      </div>
    );
  }
}
export default App;
